.about-grid {
  border-radius: 10px;
  width: 94%;
  background-color:#DFDFE6;
  font-size: 80px;
  font-weight: bold;
  box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
}

