//Import the Link API to support client-side navigation
import Link from 'next/link'
import Layout from '../components/MyLayout'
//import Image from 'react-bootstrap/Image'


/* Notice in the example below how we render static images with Next.js:
Create a folder called static in your project root directory. From your code you can then reference those files with /static/ URLs (see<img src={`/static/art/${props.data.image}.jpg`} alt=""/>) 
Note: Don't name the static directory anything else. The name is required and is the only directory that Next.js uses for serving static assets.*/
export default () => ( 
    <Layout>

<div className="welcome-section">


<div className="row">
  <div className="column" >
    <img src="/static/images/bongani.png/" alt="" className="profile-picture"/>
  </div>
  <div class="column">
        <h1>Hey I am Mimic</h1>
        <p>a web developer</p>
      </div>
</div>


      


    
       

<style jsx>
    {`
  p {
    color: black;
  }
  .profile-picture{
    width:300;
    height:300;
  }
  h1 {
    font-family: 'Raleway', sans-serif;
    font-weight: 700;
    text-align: center;
    font-size: 4rem;
  }
  h2 {
    font-family: 'Raleway', sans-serif;
    font-weight: 700;
    text-align: center;
    font-size: 4rem;
    color:white;
  }
  .column {
    float: left;
    width: 30%;
    padding: 10px;
    height: 300px; /* Should be removed. Only for demonstration */
  }
  
 
  .row:after {
    content: "";
    display: table;
    clear: both;
  }

  .welcome-section{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 60vh;
    background-color: #000;
    background-image: linear-gradient(62deg, #3a3d40 0%, #181719 100%);
    padding-top: 50px;
  }
  .welcome{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100vh;
    background-color: white;
    background-image: linear-gradient(62deg, #3a3d40 0%, #181719 100%);
    padding-top: 50px;
  }
  
  
  
  
`}
</style> 
</div>
    
    </Layout>
)
