import Link from 'next/link'
import Layout from '../components/MyLayout'
//import Beard from '../static/images/beard.png';
//import London from '../static/images/tech.png';
//import Weather from '../static/images/weather.png';

export default () => (
  <Layout>
    <div className="container">
      <div>
        <h2 className="about-heading">Projects</h2>
        <h4 className="about-intro">Link to my GitHub Repository</h4>
        <h5 className="about-intro">https://github.com/BonganiMaila</h5>
        <br/>
            <style jsx>
            {`
              .about-intro {
                font-size: 17px;
                margin-left: 2rem;
                text-align: left;
              }
              .about-heading {
                margin-left: 2rem;
                font-size: 70px;
                font-weight: lighter;
                color: black;
                font-family: 'Raleway', sans-serif;
              
              }
              
            `}
            </style>
      </div>
    

    <div className="about-intro">
    <h4 >London Tech Store</h4>
    <img src="/static/images/tech.png/" className="image" alt="tech"/> <br/>
    <style jsx>
{`
  p {
    color: black;
  }
  .about-intro {
    font-size: 17px;
    margin: 3rem;
    text-align: left;

  border-radius: 10px;
  width: 90%;
  height:600px;
  color: white;
  background-color:#ffefcf;
  font-size: 80px;
  font-weight: bold;
  box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
  }
  .image{
    padding-left:150px;
    width:800px;
    height:500px;
  }
  .about-heading {
    margin-left: 2rem;
    font-size: 70px;
    font-weight: lighter;
    color: black;
    font-family: 'Raleway', sans-serif;
   
  }
  
  
`}
</style>
    </div>

    <div className="about-intro">
    <h4>Bearded Gent</h4>
    <img src="/static/images/beard.png/" className="image" alt="beard"/> <br/>   
    <style jsx>
    {`
  p {
    color: black;
  }
  .about-intro {
    font-size: 17px;
    margin: 3rem;
    text-align: left;

  border-radius: 10px;
  width: 94%;
  color: white;
  background-color:#ffefcf;
  font-size: 80px;
  font-weight: bold;
  box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
  }
  .image{
    padding-left:150px;
    width:700px;
    height:500px;
  }
  .about-heading {
    margin-left: 2rem;
    font-size: 70px;
    font-weight: lighter;
    color: black;
    font-family: 'Raleway', sans-serif;
   
  }
  
  
`}
</style>
    </div>
    

    <div className="about-intro">
    <h4>Weather App</h4>
    <img src="/static/images/weather.png/" className="image" alt="beard"/> 
    <style jsx>
    {`
  p {
    color: black;
  }
  .about-intro {
    font-size: 17px;
    margin: 3rem;
    text-align: left;

  border-radius: 10px;
  width: 94%;
  color: white;
  background-color:#ffefcf;
  font-size: 80px;
  font-weight: bold;
  box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
  }
  .image{
    padding-left:150px;
    width:700px;
    height:450px;
  }
  .about-heading {
    margin-left: 2rem;
    font-size: 70px;
    font-weight: lighter;
    color: black;
    font-family: 'Raleway', sans-serif;
   
  }
  
  
`}
</style>   
    </div>
    

    </div>
    
  </Layout>
)
