//Import the Link API to support client-side navigation
import Link from 'next/link'
import Layout from '../components/MyLayout'
//import Image from 'react-bootstrap/Image'


/* Notice in the example below how we render static images with Next.js:
Create a folder called static in your project root directory. From your code you can then reference those files with /static/ URLs (see<img src={`/static/art/${props.data.image}.jpg`} alt=""/>) 
Note: Don't name the static directory anything else. The name is required and is the only directory that Next.js uses for serving static assets.*/
export default () => ( 
    <Layout>

<div className="welcome-section">


<div className="row">
  <div className="column1" >
    <img src="/static/images/bongani.png/" alt="" className="profile-picture"/>
  </div>
  <div class="column2">
        <h1>Hi, I am Bongani</h1>
        <h4>I'm a Web Developer</h4>
      </div>
</div>


<div className="contact-section">
      <div className="contact-section-header">
      <h3>Let's work together...</h3>
      <p>How do you take your coffee?</p>
      </div>
      </div>
      


<div className="foot">
<p>
    This is Bongani Maila's portfolio. All contact information is in the contact tab 
    
  </p>
</div>

{/*

<section id="contact" class="contact-section">
  <div class="contact-section-header">
    <h2>Let's work together...</h2>
    <p>How do you take your coffee?</p>
  </div>
  <div class="contact-links">
    <a
      href="https://facebook.com/freecodecamp"
      target="_blank"
      class="btn contact-details"
      ><i class="fab fa-facebook-square"></i> Facebook</a
    >
    <a
      id="profile-link"
      href="https://github.com/freecodecamp"
      target="_blank"
      class="btn contact-details"
      ><i class="fab fa-github"></i> GitHub</a
    >
    <a
      href="https://twitter.com/freecodecamp"
      target="_blank"
      class="btn contact-details"
      ><i class="fab fa-twitter"></i> Twitter</a
    >
    <a href="mailto:example@example.com" class="btn contact-details"
      ><i class="fas fa-at"></i> Send a mail</a
    >
    <a href="tel:555-555-5555" class="btn contact-details"
      ><i class="fas fa-mobile-alt"></i> Call me</a
    >
  </div>
</section>


*/}
      


    
       

<style jsx>
    {`
  p, h1,h4 {
    color: black;
    padding-left:50px
  }
  .profile-picture{
    width:250px;
    height:250px;
    border-radius: 50%;
  }
  h1 {
    font-family: 'Raleway', sans-serif;
    font-weight: 700;
    text-align: center;
    font-size: 4rem;
  }
  h2 {
    font-family: 'Raleway', sans-serif;
    font-weight: 700;
    text-align: center;
    font-size: 4rem;
    color:white;
  }
  .column1 {
    float: left;
    width: 35%;
    padding-right: 50px;
    height: 300px; /* Should be removed. Only for demonstration */
  }
  .column2 {
    float: left;
    width: 35%;
    padding-left: 50px;
    height: 300px; /* Should be removed. Only for demonstration */
  }
  
  
 
  .row:after {
    content: "";
    display: table;
    clear: both;
  }

  .welcome-section{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 60vh;
    background-color: #000;
    background-image: linear-gradient(62deg, #3a3d40 0%, #181719 100%);
    padding-top: 50px;
  }
  .welcome{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100vh;
    background-color: white;
    background-image: linear-gradient(62deg, #3a3d40 0%, #181719 100%);
    padding-top: 50px;
  }

  .foot {
    font-weight: 300;
    display: flex;
    justify-content: space-evenly;
    padding: 2rem;
    background: var(--main-gray);
    border-top: 4px solid var(--main-red);
}

foot>p {
    margin: 2rem;
}

foot i {
    vertical-align: middle;
}

@media (max-width: 28.75em) {
    foot {
        flex-direction: column;
        text-align: center;
    }
}

  
  
  
  
`}
</style> 
</div>
    
    </Layout>
)
